import React from 'react';
import { connect } from 'react-redux';

import ChampionCard from './ChampionCard';

var filteredChampions = this.props.champions;

class ChampionGrid extends React.Component {
    championsList() {
        

        return (
                this.props.champions.map(champion => {
                    return <ChampionCard 
                            key={champion.key}
                            name={champion.name} 
                            id={champion.key}
                            image={champion.avatar}
                        />
                })
        );
    }
    
    render() {
        return (
            <React.Fragment>
                <input type='text' id='championSearch' onkeyup='filterChampions()' placeholder='Enter a champion name (e.x. Ahri)'></input>
                <div class='row'>
                    {this.championsList()}
                </div>
            </React.Fragment>
                
        );
    }
}

const mapStateToProps = (state) => {
    return { champions: state.champions };
}

export default connect(mapStateToProps)(ChampionGrid);
