import React from 'react';
import { connect } from 'react-redux';

import ChampionCard from './ChampionCard';

class ChampionGrid extends React.Component {
    filterChampions() {
        var filteredChampions = this.props.champions;
        
        return (
                filteredChampions.map(champion => {
                    input = document.getElementById('filter_users');
                    return <ChampionCard 
                            key={champion.key}
                            name={champion.name} 
                            id={champion.key}
                            image={champion.avatar}
                        />
                })
        );
    }
    
    render() {
        return (
            <React.Fragment>
                <input type='text' id='championSearch' onKeyUp={this.filterChampions} placeholder='Enter a champion name (e.x. Ahri)'></input>
                <div className='row'>
                    {this.filterChampions()}
                </div>
            </React.Fragment>
                
        );
    }
}

const mapStateToProps = (state) => {
    return { champions: state.champions };
}

export default connect(mapStateToProps)(ChampionGrid);
